window.onerror = function (errorMsg, url, lineNumber, colNumber, errObject) {
  var stack,
    data = {
      errMsg: errorMsg,
      url: url,
      lnum: lineNumber,
      crumb: ''
    };

  if (errObject && errObject.stack && typeof errObject.stack === 'string') {
    stack = errObject.stack.split('\n');
    data.stack = JSON.stringify({last: stack[1], first: stack[stack.length - 1]});
  }

  YUI().use('io-base', function(Y) {
    Y.io('/jserror', {
      method: 'post',
      data: data
    });
  });
  return false;
};

